import "./App.css";
import { Container } from "react-bootstrap";
import Footer from "./Components/Footer";
import Header from "./Components/Header";
import Home from "./screens/Home";
import { createRoot } from "react-dom/client";
import {
  createBrowserRouter,
  RouterProvider,
  Route,
  Link,
} from "react-router-dom";
function App() {
  createBrowserRouter([
    {
      path: "/",
      element: <Root />,
      children: [
        {
          path: "contact",
          element: <Contact />,
        },
        {
          path: "dashboard",
          element: <Dashboard />,
          loader: ({ request }) =>
            fetch("/api/dashboard.json", {
              signal: request.signal,
            }),
        },
        {
          element: <AuthLayout />,
          children: [
            {
              path: "login",
              element: <Login />,
              loader: redirectIfUser,
            },
            {
              path: "logout",
              action: logoutUser,
            },
          ],
        },
      ],
    },
  ]);
